import React from 'react';
import Plan from './Plan';

class PlanCard extends React.Component{


  state={
    plans: [],
    tabState: "tab-weekly",
  }



    showWeeklyPlans = () => {
        this.setState({
            plans: this.props.weeklyPlans,
            tabState: 'tab-weekly'
        })
    }

    showFornightlyPlans = () => {
        this.setState({
            plans: this.props.fornightlyPlans,
            tabState: 'tab-fornightly'
        })
    }

    showMonthlyPlans = () => {
        this.setState({
            plans: this.props.monthlyPlans,
            tabState: 'tab-monthly'
        })
    }

    handlePlanOnclick = (id) => {
        const selectplans = this.state.plans.map(plan => {
            if(plan.id === id){
                plan.selectStateClass = "plan-card__plan-wrapper--plan  plan-selected"
            }
            return plan;
        })
        console.log(selectplans)
        this.setState({
            
        })
    }

    render(){
        let Plans;
        /* 
        To show weekly contents on page load
        */
        //handle page on load
       if(this.state.plans.length === 0){
           Plans =  this.props.weeklyPlans.map(plan => {
            //console.log(plan.id);
            return(
                <Plan
                    key = {plan.id}
                    id = {plan.id} 
                    selectState = {plan.selectStateClass}
                    paymentCount = {plan.paymentCount}
                    interval = {plan.interval}
                    planOnClick = {this.handlePlanOnclick}
                />
            )
        })
       }else{
        //handle tab on click   
        Plans = this.state.plans.map(plan => {
            //console.log(plan.id);
            return(
                <Plan
                    key = {plan.id}
                    id = {plan.id}
                    selectState = {plan.selectStateClass}
                    paymentCount = {plan.paymentCount}
                    interval = {plan.interval}
                    planOnClick = {this.handlePlanOnclick}
                />
            )
        })
       }
        
        return(
            <div className="plan-card">
               <h1> Customise your plan </h1>
               <ul className={this.state.tabState}>
                   <li className="tab" onClick={this.showWeeklyPlans}>Weekly</li>
                   <li className="tab" onClick={this.showFornightlyPlans}>Fornightly</li>
                   <li className="tab" onClick={this.showMonthlyPlans}>Monthly</li>
               </ul>
               <div className="plan-card__plan-wrapper">
                    {Plans}
               </div>
               <button className="button button--submit">Submit Plan</button>
            </div>
        );
    }
}

export default PlanCard;
